import React, { useState, useEffect } from "react";
import HomeLayout from "../../layout/HomeLayout/HomeLayout";
import Splash from "../../components/splash/splash";
import Chat from "../../components/Chat/Chat";
import illus from "../../assets/icons/back-illustration.svg";
import Expert from "../../assets/images/profile-consul.jpg";
const Index = ({ children }) => {
  const [loader, setLoader] = useState(true);

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setLoader(false);
    }, 4000);

    // Clean up the timeout when the component unmounts
    return () => clearTimeout(timeoutId);
  }, []); // Empty dependency array means this effect runs once on mount

  return (
    <HomeLayout>
      {loader ? (
        <Splash />
      ) : (
        <section
          style={{
            padding: "60px 24px 100px 24px",
            backgroundColor: "#f3f4f6",
            height: "auto",
            width: "100%",
          }}
        >
          <div
            style={{
              marginTop: "48px",
              backgroundImage: `url(${illus})`,
              backgroundColor: "#fff",
              boxShadow:
                "rgba(40, 41, 61, 0.04) 0px 0px 1.12817px, rgba(96, 97, 112, 0.16) 0px 2.25634px 4.51268px",
              paddingBottom: "62vw",
              paddingTop: "30px",
            }}
          >
            <p
              style={{
                fontFamily: "Bungee",
                letterSpacing: "-0.01em",
                fontSize: "24px",
                textAlign: "center",
                marginBottom: "0",
              }}
            >
              hi, ankit
            </p>
            <p
              style={{
                marginTop: "8px",
                lineHeight: "18px",
                opacity: "0.3",
                letterSpacing: "-0.01em",
                fontWeight: "700",
                fontSize: "14px",
                textAlign: "center",
                marginBottom: "0px",
              }}
            >
              this is your current profile
            </p>
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              <div style={{ border: "1px solid #171717", width: "100px" }}>
                <img src={Expert} />
              </div>
            </div>
            you are 0% consistent
            <img />
          </div>
        </section>
      )}
    </HomeLayout>
  );
};

export default Index;
