// Popup.js
import React from "react";
import "./popup.css";

const Popup = ({ isOpen, onClose, children }) => {
  if (!isOpen) return null;

  return (
    <div className="popup">
      <div className="popup-content">
        <div className="close-btn" onClick={onClose}>
          +
        </div>

        {children}
      </div>
    </div>
  );
};

export default Popup;
