import React, { useState, useEffect } from "react";
import HomeLayout from "../../layout/HomeLayout/HomeLayout";
import Splash from "../../components/splash/splash";
import Chat from "../../components/Chat/Chat";
import BACKILLU from "../../assets/icons/back-illustration.svg";
import PROFILECONSUL from "../../assets/images/profile-consul.jpg";
const Index = ({ children }) => {
  const [loader, setLoader] = useState(true);

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setLoader(false);
    }, 4000);

    // Clean up the timeout when the component unmounts
    return () => clearTimeout(timeoutId);
  }, []); // Empty dependency array means this effect runs once on mount

  return (
    <HomeLayout>
      {loader ? (
        <Splash />
      ) : (
        <section
          style={{
            padding: "60px 24px 100px 24px",
            backgroundColor: "#f3f4f6",
            height: "auto",
            width: "100%",
          }}
        >
          <div
            style={{
              marginTop: "48px",
              backgroundColor: "#fff",
              borderRadius: "8px",
              backgroundImage: `url(${BACKILLU})`,
            }}
          >
            <p
              style={{
                fontFamily: "Bungee",
                fontSize: "24px",
                textAlign: "center",
                marginBottom: "0px",
                letterSpacing: "-0.01em",
                paddingTop: "30px",
              }}
            >
              Hii, ANKIT
            </p>
            <p
              style={{
                lineHeight: "18px",
                opacity: "0.3",
                letterSpacing: "-0.01em",
                fontWeight: "700",
                fontSize: "14px",
                textAlign: "center",
              }}
            >
              this is your current profile
            </p>
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                margin: "24px 66px 0 66px",
                padding: "10px 8px 10px 8px",
                borderRadius: "8px",
                flexDirection: "column",
                border: " 1px solid hsla(0,0%,9%,.13)",
              }}
            >
              <img
                style={{ height: "100px", borderRadius: "8px" }}
                src={PROFILECONSUL}
              />
              <p
                style={{
                  marginTop: "8px",
                  marginBottom: "0px",
                  fontWeight: "600",
                  fontSize: "14px",
                  textAlign: "center",
                }}
              >
                Ankit
              </p>
            </div>

            <p
              style={{
                marginTop: "12px",
                marginBottom: "0px",
                textAlign: "center",
                fontSize: "14px",
                fontWeight: "500",
                padding: "14px 0 14px 0",
                fontFamily: "Space Grotesk",
              }}
            >
              you are <span style={{ color: "#f76152" }}>0%</span> consistent
            </p>
          </div>
        </section>
      )}
    </HomeLayout>
  );
};

export default Index;
