import React, { useState } from "react";
import HomeLayout from "../../layout/HomeLayout/HomeLayout";
import Splash from "../../components/splash/splash";
const Goals = () => {
  const [loading, setLoading] = useState(true);
  const dummmyTask = [
    "Wake up at 5am",
    "Read 2 article of newspaper",
    "Practice 20 DSA problems",
    "Complete Nound and Pronoun",
    "Do evening worship",
  ];
  setTimeout(() => {
    setLoading(false);
  }, 2000);
  return (
    <HomeLayout>
      {loading ? (
        <Splash />
      ) : (
        <section style={{ marginTop: "80px", padding: "0 24px 0px 24px" }}>
          {dummmyTask.map((item, id) => {
            return (
              <div
                style={{
                  backgroundColor: "#fff",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "start",
                  margin: "16px 0px 16px 0",
                  borderRadius: "8px",
                }}
              >
                <input
                  type="checkbox"
                  style={{ height: "28px", width: "28px", marginRight: "15px" }}
                />
                <p
                  style={{
                    fontSize: "26px",
                    lineHeight: "36px",
                    marginBottom: "0",
                  }}
                >
                  {item}
                </p>
              </div>
            );
          })}
        </section>
      )}
    </HomeLayout>
  );
};

export default Goals;
