import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import HomeLayout from "../../layout/HomeLayout/HomeLayout";
import Splash from "../../components/splash/splash";

const Consultation = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const inputStyles = {
    width: "100%",
    padding: "24px",
    borderRadius: "8px",
    border: "0",
    boxShadow: "0px -2px 10px rgba(0, 0, 0, 0.1)",
    transition: "box-shadow 0.3s", // Optional: Add a smooth transition effect
  };

  const handleFocus = () => {
    // Add any additional logic or styles when the input is focused
    inputStyles.boxShadow = "0px -2px 10px rgba(0, 120, 255, 0.7)"; // Example: Change the boxShadow color on focus
  };

  setTimeout(() => {
    setLoading(false);
  }, 2000);
  return (
    <HomeLayout>
      {loading ? (
        <Splash />
      ) : (
        <section
          style={{
            padding: "80px 24px 100px 24px",
            backgroundColor: "#f3f4f6",
            height: "auto",
            width: "100%",
          }}
        >
          <div
            style={{
              position: "fixed",
              bottom: 80,
              left: 0,
              width: "100%",
              padding: " 8px",
            }}
          >
            <input
              style={inputStyles}
              type="text"
              placeholder="Type Your Doubt...."
              onFocus={handleFocus}
            />
          </div>
        </section>
      )}
    </HomeLayout>
  );
};

export default Consultation;
