import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import HomeLayout from "../../layout/HomeLayout/HomeLayout";
import Splash from "../../components/splash/splash";

const questionsData = {
  questions: [
    {
      title: "Check if pair with given Sum exists in Array (Two Sum)",
      description:
        "Given an array A[ ] of n numbers and another number x, the task is to check whether or not there exist two elements in A[] whose sum is exactly x.",
      image: "",
    },
    {
      title: "Best Time to Buy and Sell Stock",
      description:
        "Given an array prices[] of length N, representing the prices of the stocks on different days, the task is to find the maximum profit possible by buying and selling the stocks on different days when at most one transaction is allowed. Note: Stock must be bought before being sold.",
      image: "",
    },
    // Add more questions here...
  ],
};

const QuestionsList = ({ questions, onQuestionClick }) => {
  return (
    <div>
      <h1>ReactJS Questions List</h1>
      <ul>
        {questions.map((question) => (
          <li key={question.title} onClick={() => onQuestionClick(question)}>
            {question.title}
          </li>
        ))}
      </ul>
    </div>
  );
};

const QuestionDetail = ({ question, onBackToList }) => {
  return (
    <div>
      <h3>{question.title}</h3>
      <p>{question.description}</p>
      <button onClick={onBackToList}>Back to List</button>
    </div>
  );
};

const App = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [selectedQuestion, setSelectedQuestion] = useState(null);

  const handleQuestionClick = (question) => {
    setSelectedQuestion(question);
  };

  const handleBackToList = () => {
    setSelectedQuestion(null);
  };

  setTimeout(() => {
    setLoading(false);
  }, 2000);

  return (
    <HomeLayout>
      {loading ? (
        <Splash />
      ) : (
        <section
          style={{
            padding: "60px 24px 100px 24px",
            backgroundColor: "#f3f4f6",
            height: "auto",
            width: "100%",
          }}
        >
          <div style={{ display: "flex" }}>
            <QuestionsList
              questions={questionsData.questions}
              onQuestionClick={handleQuestionClick}
            />
            {selectedQuestion && (
              <QuestionDetail
                question={selectedQuestion}
                onBackToList={handleBackToList}
              />
            )}
          </div>
        </section>
      )}
    </HomeLayout>
  );
};

export default App;
