import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import HomeLayout from "../../layout/HomeLayout/HomeLayout";
import Splash from "../../components/splash/splash";
const questionsData = {
  questions: [
    {
      title: "Check if pair with given Sum exists in Array (Two Sum)",
      description:
        "Given an array A[ ] of n numbers and another number x, the task is to check whether or not there exist two elements in A[] whose sum is exactly x.",
      examples: [
        {
          input: { arr: [0, -1, 2, -3, 1], x: -2 },
          output: "Yes",
          explanation: "If we calculate the sum of the output, 1 + (-3) = -2",
        },
        {
          input: { arr: [1, -2, 1, 0, 5], x: 0 },
          output: "No",
          explanation:
            "Since the array is in decreasing order, no possible way exists to solve the problem.",
        },
      ],
    },
    {
      title: "Best Time to Buy and Sell Stock",
      description:
        "Given an array prices[] of length N, representing the prices of the stocks on different days, the task is to find the maximum profit possible by buying and selling the stocks on different days when at most one transaction is allowed. Note: Stock must be bought before being sold.",
      examples: [
        {
          input: { prices: [7, 1, 5, 3, 6, 4] },
          output: 5,
          explanation:
            "The lowest price of the stock is on the 2nd day, i.e. price = 1. Starting from the 2nd day, the highest price of the stock is witnessed on the 5th day, i.e. price = 6. Therefore, the maximum possible profit = 6 – 1 = 5.",
        },
        {
          input: { prices: [7, 6, 4, 3, 1] },
          output: 0,
          explanation:
            "Since the array is in decreasing order, no possible way exists to solve the problem.",
        },
      ],
    },
    // ... Add more questions here ...
  ],
};

const QuestionsList = ({ questions }) => {
  const [loading, setLoading] = useState(true);
  return (
    <div>
      {questions.map((question, index) => (
        <div key={index} style={{ marginBottom: "20px" }}>
          <h3>{question.title}</h3>
          <p>{question.description}</p>
          <ul>
            {question.examples.map((example, exampleIndex) => (
              <li key={exampleIndex}>
                <strong>Example {exampleIndex + 1}:</strong>
                <ul>
                  <li>
                    <strong>Input:</strong> {JSON.stringify(example.input)}
                  </li>
                  <li>
                    <strong>Output:</strong> {JSON.stringify(example.output)}
                  </li>
                  {example.explanation && (
                    <li>
                      <strong>Explanation:</strong> {example.explanation}
                    </li>
                  )}
                </ul>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

const App = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  setTimeout(() => {
    setLoading(false);
  }, 2000);
  return (
    <HomeLayout>
      {loading ? (
        <Splash />
      ) : (
        <section
          style={{
            padding: "60px 24px 100px 24px",
            backgroundColor: "#f3f4f6",
            height: "auto",
            width: "100%",
          }}
        >
          <div>
            <h1>ReactJS Questions List</h1>
            <QuestionsList questions={questionsData.questions} />
          </div>
        </section>
      )}
    </HomeLayout>
  );
};

export default App;
