import React, { useState, useEffect } from "react";
import HomeLayout from "../../layout/HomeLayout/HomeLayout";
import Splash from "../../components/splash/splash";
import Chat from "../../components/Chat/Chat";
import illus from "../../assets/icons/back-illustration.svg";
import Expert from "../../assets/images/profile-consul.jpg";
import Scolding from "../../assets/images/scolding.png";
const Index = ({ children }) => {
  const [loader, setLoader] = useState(true);

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setLoader(false);
    }, 4000);

    // Clean up the timeout when the component unmounts
    return () => clearTimeout(timeoutId);
  }, []); // Empty dependency array means this effect runs once on mount

  return (
    <HomeLayout>
      {loader ? (
        <Splash />
      ) : (
        <section
          style={{
            padding: "60px 24px 100px 24px",
            backgroundColor: "#f3f4f6",
            height: "auto",
            width: "100%",
          }}
        >
          <div
            style={{
              marginTop: "48px",
              backgroundImage: `url(${illus})`,
              backgroundColor: "#fff",
              boxShadow:
                "rgba(40, 41, 61, 0.04) 0px 0px 1.12817px, rgba(96, 97, 112, 0.16) 0px 2.25634px 4.51268px",
              paddingBottom: "62vw",
              paddingTop: "30px",
              borderRadius: "8px",
              height: "532px",
            }}
          >
            <p
              style={{
                fontFamily: "Bungee",
                letterSpacing: "-0.01em",
                fontSize: "24px",
                textAlign: "center",
                marginBottom: "0",
              }}
            >
              hi, ankit
            </p>
            <p
              style={{
                marginTop: "8px",
                lineHeight: "18px",
                opacity: "0.3",
                letterSpacing: "-0.01em",
                fontWeight: "700",
                fontSize: "14px",
                textAlign: "center",
                marginBottom: "0px",
              }}
            >
              this is your current profile
            </p>
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              <div
                style={{
                  border: "1px solid hsla(0,0%,9%,.13)",
                  marginTop: "24px",
                  padding: "8px",
                  borderRadius: "8px  ",
                }}
              >
                <img style={{ width: "100px", height: "80px" }} src={Expert} />
              </div>
            </div>
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                marginTop: "14px",
              }}
            >
              <p
                style={{
                  textAlign: "center",
                  marginBottom: "0",
                  backgroundColor: "#fff",
                  fontFamily: "Space Grotesk",
                  padding: "14px",
                  borderRadius: "8px",
                  fontWeight: "500",
                  fontSize: "12px",
                  border: " 1px solid rgba(23, 23, 23, 0.13)",
                  width: "60%",
                }}
              >
                you are <span style={{ color: "#f76152" }}> 0% </span>consistent
              </p>
            </div>

            <img style={{ width: "100%", marginTop: "22px" }} src={Scolding} />
          </div>
          <div
            style={{
              backgroundColor: "#17AD7D",
              marginTop: "28px",
              borderRadius: "8px",
            }}
          >
            <p style={{ marginBottom: "0" }}>
              no concern is too small, ask an expert
            </p>
            <p
              style={{
                marginBottom: "0",
                fontFamily: "Space Grotesk",
                lineHeight: "18px",
                color: "#fff",
                fontWeight: "600",
                fontSize: "14px",
              }}
            >
              24/7 support from our trusted experts
            </p>
            <p
              style={{
                marginBottom: "0",
                fontFamily: "Space Grotesk",
                lineHeight: "18px",
                color: "#fff",
                fontWeight: "600",
                fontSize: "14px",
              }}
            >
              100% money back guarantee!
            </p>
            <p style={{ marginBottom: "0" }}>comming soon!</p>
          </div>
        </section>
      )}
    </HomeLayout>
  );
};

export default Index;
