import React, { useEffect, useState } from "react";
import "./banner.css"; // Import the CSS file

const Banner = () => {
  const [showBanner, setShowBanner] = useState(false); // Initially, the banner is not shown

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowBanner(true); // After 4 seconds, the banner is shown
    }, 4000);

    return () => {
      clearTimeout(timer); // Clear the timer when the component is unmounted
    };
  }, []); // The empty array means this effect runs once after the initial render

  return (
    <>
      {showBanner && (
        <div className="banner">
          <div
            style={{ height: "400px", backgroundColor: "#fff", width: "300px" }}
          ></div>
          <h1>Your Banner Content</h1>
          <button onClick={() => setShowBanner(false)}>Cancel</button>
        </div>
      )}
    </>
  );
};

export default Banner;
